contracts/CarbonMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "./CarbonToken.sol";

contract CarbonMarketplace is CarbonToken {
    struct Listing {
        uint256 amount; // Number of tokens to sell
        uint256 price;  // Price per token in wei
        bool active;    // Is the listing active or not
    }

    mapping(address => Listing) public listings;
    address[] public sellers;

    event TokensListed(address indexed seller, uint256 amount, uint256 price);
    event ListingUpdated(address indexed seller, uint256 newAmount, uint256 newPrice);
    event TokensPurchased(address indexed buyer, address indexed seller, uint256 amount, uint256 totalPrice);

    // Function to list tokens for sale
    function listTokens(uint256 amount, uint256 price) external {
        require(balanceOf(msg.sender) >= amount, "Insufficient token balance");
        require(amount > 0 && price > 0, "Amount and price must be greater than zero");

        // If it's the user's first listing, add them to the seller's array
        if (!listings[msg.sender].active) {
            sellers.push(msg.sender);
        }

        listings[msg.sender] = Listing({
            amount: amount,
            price: price,
            active: true
        });

        emit TokensListed(msg.sender, amount, price);
    }

    // Function to update a token listing
    function updateListing(uint256 newAmount, uint256 newPrice) external {
        require(listings[msg.sender].active, "You do not have an active listing");
        require(balanceOf(msg.sender) >= newAmount, "Insufficient token balance");
        require(newAmount > 0 && newPrice > 0, "Amount and price must be greater than zero");

        listings[msg.sender].amount = newAmount;
        listings[msg.sender].price = newPrice;

        emit ListingUpdated(msg.sender, newAmount, newPrice);
    }

    // Function to calculate the average price of a single token across all active listings
    function calculateAveragePrice() public view returns (uint256) {
        uint256 totalTokens = 0;
        uint256 totalValue = 0;

        for (uint256 i = 0; i < sellers.length; i++) {
            address seller = sellers[i];
            if (listings[seller].active) {
                totalTokens += listings[seller].amount;
                totalValue += listings[seller].amount * listings[seller].price;
            }
        }

        require(totalTokens > 0, "No tokens listed for sale");

        return totalValue / totalTokens; // Average price per token
    }

    // Function to buy tokens from a seller
    function buyTokens(address seller, uint256 amount) external payable {
        require(listings[seller].active, "Seller does not have an active listing");
        require(listings[seller].amount >= amount, "Not enough tokens listed");
        uint256 totalPrice = listings[seller].price * amount;
        require(msg.value == totalPrice, "Incorrect ETH amount sent");

        // Transfer tokens to the buyer
        _transfer(seller, msg.sender, amount);

        // Update seller's listing
        listings[seller].amount -= amount;
        if (listings[seller].amount == 0) {
            listings[seller].active = false;  // Deactivate listing if all tokens are sold
        }

        // Transfer ETH to the seller
        payable(seller).transfer(msg.value);

        emit TokensPurchased(msg.sender, seller, amount, totalPrice);
    }

    // Function to view a seller's active listing
    function viewListing(address seller) external view returns (Listing memory) {
        return listings[seller];
    }

    // Function to get the number of active sellers
    function getNumberOfSellers() external view returns (uint256) {
        return sellers.length;
    }

    // New function to view all active listings with wallet, amount, and price
    function getAllListings() external view returns (address[] memory, uint256[] memory, uint256[] memory) {
        uint256 activeListings = 0;

        // First, count how many active listings there are
        for (uint256 i = 0; i < sellers.length; i++) {
            if (listings[sellers[i]].active) {
                activeListings++;
            }
        }

        // Create arrays to store the listing details
        address[] memory walletAddresses = new address[](activeListings);
        uint256[] memory amounts = new uint256[](activeListings);
        uint256[] memory prices = new uint256[](activeListings);

        // Fill the arrays with data from active listings
        uint256 index = 0;
        for (uint256 i = 0; i < sellers.length; i++) {
            if (listings[sellers[i]].active) {
                walletAddresses[index] = sellers[i];
                amounts[index] = listings[sellers[i]].amount;
                prices[index] = listings[sellers[i]].price;
                index++;
            }
        }

        return (walletAddresses, amounts, prices);
    }
}

contracts/CarbonToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract CarbonToken is ERC20, Ownable {
    struct Certificate {
        string message;
        uint256 timestamp;
        address issuedTo;
    }

    mapping(address => Certificate[]) public certificates;

    event TokensMinted(address indexed to, uint256 amount);
    event TokensBurned(address indexed from, uint256 amount);
    event CertificateIssued(address indexed to, string message);

    constructor() ERC20("CarbonToken", "CARB") Ownable(msg.sender) {}

    // Function to mint tokens to a specific user (only admin can call)
    function mintTokens(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
        emit TokensMinted(to, amount);
    }

    // Function to burn tokens from a user's account (only admin can call)
    function burnTokens(address from, uint256 amount) external onlyOwner {
        _burn(from, amount);
        emit TokensBurned(from, amount);

        // Issue certificate after burning tokens
        _issueCertificate(from, amount);
    }

    // Internal function to issue a certificate
    function _issueCertificate(address to, uint256 burnedAmount) internal {
        string memory certificateMessage = string(
            abi.encodePacked(
                "Certificate: ",
                "Project approved. ",
                burnedAmount,
                " tokens burned successfully."
            )
        );

        Certificate memory newCertificate = Certificate({
            message: certificateMessage,
            timestamp: block.timestamp,
            issuedTo: to
        });

        certificates[to].push(newCertificate);

        emit CertificateIssued(to, certificateMessage);
    }

    // Function to view certificates of a user
    function getCertificates(address user) external view returns (Certificate[] memory) {
        return certificates[user];
    }
}

contracts/ProjectDonation.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "./CarbonToken.sol";

contract ProjectDonation is CarbonToken {
    struct Project {
        string name;
        string website;
        string description;
        uint256 tokenRequired;
        uint256 tokenReceived;
        bool isActive;
        address creator;
    }

    Project[] public projects;

    event ProjectCreated(uint256 indexed projectId, string name, uint256 tokenRequired);
    event DonationReceived(uint256 indexed projectId, address donor, uint256 amount);
    event ProjectFunded(uint256 indexed projectId);

    // Modifier to check if the project is active
    modifier isActiveProject(uint256 projectId) {
        require(projectId < projects.length, "Project does not exist.");
        require(projects[projectId].isActive, "Project is not active.");
        _;
    }

    // Function to create a new project
    function createProject(
        string memory name,
        string memory website,
        string memory description,
        uint256 tokenRequired
    ) external {
        require(tokenRequired > 0, "Token requirement must be greater than zero.");

        projects.push(Project({
            name: name,
            website: website,
            description: description,
            tokenRequired: tokenRequired,
            tokenReceived: 0,
            isActive: true,
            creator: msg.sender
        }));

        emit ProjectCreated(projects.length - 1, name, tokenRequired);
    }

    // Function to view all active projects
    function getActiveProjects() external view returns (Project[] memory) {
        uint256 activeCount = 0;

        // Count the number of active projects
        for (uint256 i = 0; i < projects.length; i++) {
            if (projects[i].isActive) {
                activeCount++;
            }
        }

        // Create an array to store active projects
        Project[] memory activeProjects = new Project[](activeCount);
        uint256 j = 0;

        // Populate the active projects array
        for (uint256 i = 0; i < projects.length; i++) {
            if (projects[i].isActive) {
                activeProjects[j] = projects[i];
                j++;
            }
        }

        return activeProjects;
    }

    // // Function to donate to a project
    // function donateToProject(uint256 projectId, uint256 amount) external isActiveProject(projectId) {
    //     Project storage project = projects[projectId];

    //     // Ensure the donation does not exceed the required tokens
    //     require(amount > 0, "Donation must be greater than zero.");
    //     require(project.tokenReceived + amount <= project.tokenRequired, "Donation exceeds required amount.");

    //     // Transfer tokens from the donor to this contract
    //     _transfer(msg.sender, address(this), amount);

    //     // Update project token received
    //     project.tokenReceived += amount;

    //     emit DonationReceived(projectId, msg.sender, amount);

    //     // If the project has received the required tokens, mark it as funded
    //     if (project.tokenReceived >= project.tokenRequired) {
    //         project.isActive = false;
    //         emit ProjectFunded(projectId);
    //     }
    // }
    // Function to donate to a project
function donateToProject(uint256 projectId, uint256 amount) external isActiveProject(projectId) {
    Project storage project = projects[projectId];

    // Ensure the donation is greater than zero
    require(amount > 0, "Donation must be greater than zero.");

    uint256 excessAmount = 0;
    uint256 remainingTokensNeeded = project.tokenRequired - project.tokenReceived;

    // If the donation exceeds the required amount, calculate the excess
    if (amount > remainingTokensNeeded) {
        excessAmount = amount - remainingTokensNeeded;
        amount = remainingTokensNeeded; // Only donate the needed amount
    }

    // Transfer the needed amount of tokens from the donor to this contract
    _transfer(msg.sender, address(this), amount);

    // Update project token received
    project.tokenReceived += amount;

    emit DonationReceived(projectId, msg.sender, amount);

    // If the project has received the required tokens, mark it as funded
    if (project.tokenReceived >= project.tokenRequired) {
        project.isActive = false;
        emit ProjectFunded(projectId);
    }

    // Refund the excess amount to the donor, if any
    if (excessAmount > 0) {
        _transfer(address(this), msg.sender, excessAmount);
    }
}


    // Function to get details of a project by its ID
    function getProject(uint256 projectId) external view returns (Project memory) {
        require(projectId < projects.length, "Project does not exist.");
        return projects[projectId];
    }
}

